    private void jButtonConcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonConcluirActionPerformed
        FacadeRnaMLP.setNeuroniosCamada(jTextFieldNeuroniosCamada.getText());
        FacadeRnaMLP.setFuncaoAtivacao((String)jComboBoxFuncaoAtivacao.getSelectedItem());
        FacadeRnaMLP.setTxAprendizado(Double.parseDouble(jFormattedTextFieldTxAprendizado.getText()));
        FacadeRnaMLP.setMomento(Double.parseDouble(jFormattedTextFieldMomento.getText()));
        FacadeRnaMLP.setEpocas(Integer.parseInt(jFormattedTextFieldEpocas.getText()));
        FacadeRnaMLP.setErroMinimo(Double.parseDouble(jFormattedTextFieldErroMinimo.getText()));
        
        
        FacadeRnaMLP.getJDialogConfig().dispose();
    }//GEN-LAST:event_jButtonConcluirActionPerformed

