    private void jButtonConcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonConcluirActionPerformed
        FacadeSVM.setC(             Double.parseDouble(jTextFieldC.getText()));
        FacadeSVM.setEps(           Double.parseDouble(jTextFieldEps.getText()));
        FacadeSVM.setTolerance(     Double.parseDouble(jTextFieldTolerance.getText()));
        FacadeSVM.setSigma(         Double.parseDouble(jTextFieldSigma.getText()));
        FacadeSVM.setIsBinary(      jCheckBoxDadosBinarios.isSelected());
        FacadeSVM.setIsEsparceData( jCheckBoxDadosEsparcos.isSelected());
        FacadeSVM.setIsLinearKernel(jCheckBoxDadosEsparcos.isSelected());
        FacadeSVM.getJDialogConfig().dispose();
    }//GEN-LAST:event_jButtonConcluirActionPerformed


